mapper.select:
select a.artist_id, (select name from TB_USER where user_id = a.artist_id) artist_name, (select name from TB_USER where user_id = a.artist_id) sentence_name, a.sentence, a.file_group_seq, a.upload_date, a.painting_id, a.posted_num, a.painting_status, a.posted_people_cnt from TB_PAINTING a where a.artist_id in (select following from TB_FOLLOW where user_id = #{userId}) and a.painting_status in #{status}
and a.private_at = 'N' union all select a.artist_id, (select name from TB_USER where user_id = a.artist_id) artist_name, (select name from TB_USER where user_id = b.user_id) sentence_name, b.sentence, a.file_group_seq, b.purchase_date, a.painting_id, a.posted_num, a.painting_status, a.posted_people_cnt from TB_PAINTING a, TB_PURCHASE b where a.painting_id = b.painting_id and a.private_at = 'N' and b.private_at = #{privateAt} and b.purchase_status in #{status}
and b.user_id in (select following from TB_FOLLOW where user_id = #{userId}) order by upload_date desc limit #{startRow}, #{rowPerPage}

mapper.insert:
delete from TB_FOLLOW
 where user_id = #{userId}
   and following = (select user_id from TB_USER where name = #{following})
